mapper.select:
select exists(select 1
              from enrollment Force INDEX (enrollment_student_identifier_course_offering_id_index)
              where student_identifier = #{studentIdentifier}
                and course_offering_id = #{courseOfferingId}
                and deleted = 0)

mapper.delete:
delete from enrollment where enrollment.student_identifier and enrollment.course_offering_id in

    #{item.studentIdentifier},#{item.courseOfferingId}

and deleted = 0
